mapper.update:
update address_book
user_id = #{userId}, consignee = #{consignee}, phone = #{phone}, sex = #{sex}, province_code = #{provinceCode}, province_name = #{provinceName}, city_code = #{cityCode}, city_name = #{cityName}, district_code = #{districtCode}, district_name = #{districtName}, detail = #{detail}, label = #{label}, is_default = #{isDefault},
where id = #{id}

mapper.select:
select * from address_book


        and user_id = #{userId}


        and is_default = #{isDefault}
