mapper.update:
update sky_take_out.employee
set status = #{status}
where id = #{id}

update sky_take_out.employee
name = #{name}, username = #{username}, password = #{password}, phone = #{phone}, sex = #{sex}, id_Number = #{idNumber}, update_Time = #{updateTime}, update_User = #{updateUser}, status = #{status},
where id = #{id}

mapper.select:
select * from sky_take_out.employee


        name like concat(#{name},'%')


limit #{page},#{pageSize}
